import telebot
from telebot import types
bot = telebot.TeleBot('7815451322:AAHTk_va1S6ZzV7bqjBhycEKOOAtogGh0tY')
@bot.message_handler(commands=['start'])
def start(message):

    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
    btn1 = types.KeyboardButton("üëã –ü–æ–∑–¥–æ—Ä–æ–≤–∞—Ç—å—Å—è")
    markup.add(btn1)
    bot.send_message(message.from_user.id, "üëã –ü—Ä–∏–≤–µ—Ç! –Ø —Ç–≤–æ–π –±–æ—Ç-–ø–æ–º–æ—à–Ω–∏–∫!", reply_markup=markup)

@bot.message_handler(content_types=['text'])

        
def get_text_messages(message):

    if message.text == 'üëã –ü–æ–∑–¥–æ—Ä–æ–≤–∞—Ç—å—Å—è' or message.text == '–ù–∞–∑–∞–¥':
        markup = types.ReplyKeyboardMarkup(resize_keyboard=True) #—Å–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤—ã—Ö –∫–Ω–æ–ø–æ–∫
        btn1 = types.KeyboardButton('–ß—Ç–æ —Ç–∞–∫–æ–µ –°++?')
        btn2 = types.KeyboardButton('–ò—Å—Ç–æ—Ä–∏—è C++?')
        btn3 = types.KeyboardButton('–û–±—É—á–µ–Ω–∏–µ –°++')
        markup.add(btn1, btn2, btn3)
        bot.send_message(message.from_user.id, '‚ùì –ó–∞–¥–∞–π—Ç–µ –∏–Ω—Ç–µ—Ä–µ—Å—É—é—â–∏–π –≤–∞—Å –≤–æ–ø—Ä–æ—Å', reply_markup=markup) #–æ—Ç–≤–µ—Ç –±–æ—Ç–∞


    elif message.text == '–ß—Ç–æ —Ç–∞–∫–æ–µ –°++?':
        bot.send_message(message.from_user.id, '–≠—Ç–æ –∫–æ–º–ø–∏–ª–∏—Ä—É–µ–º—ã–π —Å—Ç–∞—Ç–∏—á–µ—Å–∫–∏ —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —è–∑—ã–∫ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏—è –æ–±—â–µ–≥–æ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏—è. –ë—ã–ª —Ä–∞–∑—Ä–∞–±–æ—Ç–∞–Ω –ë—å—è—Ä–Ω–µ –°—Ç—Ä–∞—É—Å—Ç—Ä—É–ø–æ–º –≤ –Ω–∞—á–∞–ª–µ 1980-—Ö –≥–æ–¥–æ–≤ –∫–∞–∫ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–µ —è–∑—ã–∫–∞ C (–∏ –¥–∞–∂–µ –∏–∑–Ω–∞—á–∞–ª—å–Ω–æ –Ω–∞–∑—ã–≤–∞–ª—Å—è ¬´–°–∏ —Å –∫–ª–∞—Å—Å–∞–º–∏¬ª)' )

    elif message.text == '–ò—Å—Ç–æ—Ä–∏—è C++?':
        bot.send_message(message.from_user.id, '–ò—Å—Ç–æ—Ä–∏—è —è–∑—ã–∫–∞ C++ –Ω–∞—á–∏–Ω–∞–µ—Ç—Å—è –≤ –Ω–∞—á–∞–ª–µ 1980-—Ö –≥–æ–¥–æ–≤, –∫–æ–≥–¥–∞ –¥–∞—Ç—Å–∫–∏–π –ø—Ä–æ–≥—Ä–∞–º–º–∏—Å—Ç –ë—å—ë—Ä–Ω –°—Ç—Ä–∞—É—Å—Ç—Ä—É–ø —Ä–∞–∑—Ä–∞–±–æ—Ç–∞–ª –µ–≥–æ –∫–∞–∫ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–µ –∫ —è–∑—ã–∫—É –°–∏. –§–∞–∫—Ç–∏—á–µ—Å–∫–∏ –≤–Ω–∞—á–∞–ª–µ C++ –ø—Ä–æ—Å—Ç–æ –¥–æ–ø–æ–ª–Ω—è–ª —è–∑—ã–∫ –°–∏ –Ω–µ–∫–æ—Ç–æ—Ä—ã–º–∏ –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—è–º–∏ –æ–±—ä–µ–∫—Ç–Ω–æ-–æ—Ä–∏–µ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏—è.')

    elif message.text == '–û–±—É—á–µ–Ω–∏–µ –°++':
        markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
        btn4 = types.KeyboardButton('–ë–∞–∑–æ–≤—ã–µ —Ç–∏–ø—ã –≤–≤–æ–¥–∞, –≤—ã–≤–æ–¥–∞')
        btn5 = types.KeyboardButton('–¶–∏–∫–ª—ã')
        btn6 = types.KeyboardButton('–û–ø–µ—Ä–∞—Ç–æ—Ä—ã')
        btn7 = types.KeyboardButton('–ù–∞–∑–∞–¥')
        markup.add(btn4, btn5, btn6,btn7)
        bot.send_message(message.from_user.id, "–í—ã–±–µ—Ä–∏ –∏–Ω—Ç–µ—Ä–µ—Å—É—é—â–∏—é —Ç–µ–º—É", reply_markup=markup)
    elif message.text == '–ë–∞–∑–æ–≤—ã–µ —Ç–∏–ø—ã –≤–≤–æ–¥–∞, –≤—ã–≤–æ–¥–∞':
        bot.send_message(message.from_user.id, '–í C++ –æ–ø–µ—Ä–∞—Ü–∏–∏ –≤–≤–æ–¥–∞ –∏ –≤—ã–≤–æ–¥–∞ —Ä–µ–∞–ª–∏–∑–æ–≤–∞–Ω—ã —Å –ø–æ–º–æ—â—å—é –ø–æ—Ç–æ–∫–æ–≤. –ü–æ—Ç–æ–∫ ‚Äî —ç—Ç–æ –æ–±—ä–µ–∫—Ç, –≤ –∫–æ—Ç–æ—Ä—ã–π –ø—Ä–æ–≥—Ä–∞–º–º–∞ –º–æ–∂–µ—Ç –≤—Å—Ç–∞–≤–ª—è—Ç—å –∏–ª–∏ –∏–∑–≤–ª–µ–∫–∞—Ç—å —Å–∏–º–≤–æ–ª—ã.\n' + '–°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–µ –ø–æ—Ç–æ–∫–∏:\n cin ‚Äî –æ–±—ä–µ–∫—Ç –∫–ª–∞—Å—Å–∞ istream, —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–π —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω–æ–º—É –≤–≤–æ–¥—É. –í –æ–±—â–µ–º —Å–ª—É—á–∞–µ –æ–Ω –ø–æ–∑–≤–æ–ª—è–µ—Ç —á–∏—Ç–∞—Ç—å –¥–∞–Ω–Ω—ã–µ —Å —Ç–µ—Ä–º–∏–Ω–∞–ª–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è\n cout ‚Äî –æ–±—ä–µ–∫—Ç –∫–ª–∞—Å—Å–∞ ostream, —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–π —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω–æ–º—É –≤—ã–≤–æ–¥—É. –í –æ–±—â–µ–º —Å–ª—É—á–∞–µ –æ–Ω –ø–æ–∑–≤–æ–ª—è–µ—Ç –≤—ã–≤–æ–¥–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –Ω–∞ —Ç–µ—Ä–º–∏–Ω–∞–ª –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è\n–ü—Ä–∏–º–µ—Ä:\nstd::cout<< i << std::endl;')
    elif message.text == '–¶–∏–∫–ª—ã':
        bot.send_message(message.from_user.id, '–¶–∏–∫–ª—ã –≤ C++ –ø–æ–∑–≤–æ–ª—è—é—Ç –≤—ã–ø–æ–ª–Ω—è—Ç—å –æ–¥–Ω–æ –¥–µ–π—Å—Ç–≤–∏–µ –Ω–µ—Å–∫–æ–ª—å–∫–æ —Ä–∞–∑ –ø–æ–¥—Ä—è–¥. –° –∏—Ö –ø–æ–º–æ—â—å—é –º–æ–∂–Ω–æ –∞–≤—Ç–æ–º–∞—Ç–∏–∑–∏—Ä–æ–≤–∞—Ç—å –∑–∞–¥–∞—á–∏, –ø–∞—Ä–æ–π —Å—Ç—Ä–æ–∫ –∫–æ–¥–∞ –≤—ã–ø–æ–ª–Ω—è—Ç—å –Ω–µ—Å–∫–æ–ª—å–∫–æ –¥–µ–π—Å—Ç–≤–∏–π –∏ –≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å –¥–∞–Ω–Ω—ã–µ\n–ü—Ä–∏–º–µ—Ä:\nfor(int i; i<10;i++){std::cout<< i << std::endl;}')
    elif message.text == '–û–ø–µ—Ä–∞—Ç–æ—Ä—ã':
        bot.send_message(message.from_user.id, '–≠—Ç–æ —Å–æ—é–∑—ã, –∫–æ—Ç–æ—Ä—ã–µ –ø–æ–∑–≤–æ–ª—è—é—Ç –æ–±—ä–µ–¥–∏–Ω—è—Ç—å –ø–æ –Ω–µ—Å–∫–æ–ª—å–∫–æ —É—Å–ª–æ–≤–∏–π. –í Python –µ—Å—Ç—å –≤—Å–µ–≥–æ —Ç—Ä–∏ –æ–ø–µ—Ä–∞—Ç–æ—Ä–∞: and (–∏), or (–∏–ª–∏) –∏ not (–Ω–µ). –ò (and). –ï—Å–ª–∏ —É—Å–ª–æ–≤–∏—è —Å –¥–≤—É—Ö —Å—Ç–æ—Ä–æ–Ω –æ–ø–µ—Ä–∞—Ç–æ—Ä–∞ and –∏—Å—Ç–∏–Ω–Ω—ã, —Ç–æ–≥–¥–∞ –≤—Å–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ —Ü–µ–ª–∏–∫–æ–º —Å—á–∏—Ç–∞–µ—Ç—Å—è –∏—Å—Ç–∏–Ω–Ω—ã–º. –ü—Ä–∏–º–µ—Ä: if (a > 7 and b<8) std::cout << (a) False –ò–ª–∏ (or) << std::endl; –í—ã—Ä–∞–∂–µ–Ω–∏–µ –ª–æ–∂–Ω–æ, –µ—Å–ª–∏ –æ–±–∞ –æ–ø–µ—Ä–∞–Ω–¥–∞ —Å –¥–≤—É—Ö —Å—Ç–æ—Ä–æ–Ω –ª–æ–∂–Ω—ã–µ. –ï—Å–ª–∏ —Ö–æ—Ç—è –±—ã –æ–¥–Ω–æ –∏–∑ –Ω–∏—Ö –∏—Å—Ç–∏–Ω–Ω–æ–µ, —Ç–æ –∏ –≤—Å–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ –∏—Å—Ç–∏–Ω–Ω–æ.')


bot.polling(none_stop=True, interval=0) #–æ–±—è–∑–∞—Ç–µ–ª—å–Ω–∞—è –¥–ª—è —Ä–∞–±–æ—Ç—ã –±–æ—Ç–∞ —á–∞—Å—Ç—å
